import cx from 'classnames'
import React from 'react';
import PropTypes from 'prop-types'
import {Colors} from '../colors'

import styles from './styles.css'

const Buttons = function (props) {

  let {textColor, buttonText, ...passThrough} = props

  let text = {
    navy: Colors['navy'],
    white: "#FFFFFF",
    grey: Colors['grey']
  }

  let color = {
    color: text[props.textColor],
    borderColor: text[props.textColor]
  }

  return (
    <button className={styles.ghostButton} style={color} {...passThrough}>
      {props.buttonText}
    </button>
  )
}

export default function GhostButton (props){
  let {color, buttonText, ...passThrough} = props
  return (
    <Buttons
      buttonText={buttonText}
      color={color}
      {...passThrough}
    />
  )
}
