import React from 'react'
import _ from 'lodash'

import ProfilePolygon from './profile-polygon'

export class GreatBrainPolygon extends React.Component{
  _getRawScore(value){
    const map = {
      1: 16,
      2: 50,
      3: 84,
      4: 118
    }
    return map[value]
  }

  render(){
    const scores = _.mapValues(this.props.values, this._getRawScore)
    return (
      <ProfilePolygon scores={scores} strokeWidth={5}/>
    )
  }
}
