import cx from 'classnames'
import React from 'react';
import PropTypes from 'prop-types'
import _ from 'lodash'

import styles from './styles.css'
import {Colors} from '../colors'

export default class RadioButtonQuiz extends React.Component {

  constructor(props) {
    super();
    this.state = {
      isChecked: null
    };
  }

  check(option) {
    this.setState({
      isChecked: option
    })
  }

  render(props){

    return (
      <form>
        {this.props.options.map( option => {

          let optionId = option.split(" ").join("")

          return (
            <div key={optionId} onClick={()=>this.check(option)} className={styles.optionWrapper}>
              <input type="radio" id={optionId} name={this.props.formName} value={option}/>
              <label for={optionId}>{option}</label>
            </div>
          )
        })}
      </form>
    )
  }
}

RadioButtonQuiz.propTypes = {
  options: PropTypes.node,
  formName: PropTypes.string
}
