import React from 'react';
import Tabs from '../index.js';
import {shallow, configure} from 'enzyme'
import Adapter from 'enzyme-adapter-react-16'

configure({adapter: new Adapter()})

describe('tab contents', () => {
  it('should only render the content of one tab at a time, and the content should match the content input for the first tab', () => {
    let tabsList = <Tabs
        tabs={[{
          tab1: {
            label: 'Group 1',
            content: `Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec turpis sem, pharetra a nulla eget, molestie fringilla arcu. Maecenas et quam cursus eros iaculis ornare.`
          },
          tab2: {
            label: 'Group 2',
            content: `Vestibulum ultrices risus eu est imperdiet ultrices. Nam tincidunt enim non tempus vestibulum. Maecenas ullamcorper diam lobortis est interdum mollis.`
          },
          tab3: {
            label: 'Group 3',
            content: `Sed eu quam id tortor venenatis porta.`
          },
          tab4: {
            label: 'Group 4',
            content: `Suspendisse ut ex elementum nulla luctus pellentesque non scelerisque magna.`
          }}]
        }
      />

    const wrapper = shallow(tabsList);
    const tabs = wrapper.find("[data-qa-id='content']");

    const tabObj = tabsList.props.tabs[0];

    expect(tabs).toHaveLength(1);
    expect(tabs.text()).toMatch(tabObj.tab1.content)
  })
});
